mapper.select:
select sri.id, sri.table_id, sri.shift_id, sri.route_item_no, sri.attendance_station_id, sri.attendance_at, sri.meet_at, sri.meet_runtime_item_id, sri.meet_station_id, sri.back_runtime_item_id, sri.back_station_id, sri.back_at, sri.distance, sri.remark, sri.description, sri.create_at, mri.train_no meet_train_no, bri.train_no back_train_no, GROUP_CONCAT(rri.train_no SEPARATOR '--->') train_no_descriptions from sb_route_item sri left join sb_runtime_item mri on mri.id = sri.meet_runtime_item_id left join sb_runtime_item bri on bri.id = sri.back_runtime_item_id left join sb_route_runtime_relation srrr on srrr.route_item_id = sri.id left join sb_runtime_item rri on rri.id = srrr.runtime_item_id
1=1
and sri.table_id = #{tableId}
and sri.shift_id = #{shiftId}
and sri.route_item_no = #{routeItemNo}
and sri.attendance_station_id = #{attendanceStationId}
and sri.meet_train_no = #{meetTrainNo}
and sri.meet_station_id = #{meetStationId}
and sri.back_train_no = #{backTrainNo}
and sri.back_station_id = #{backStationId}
group by sri.id order by sri.id asc
