mapper.select:
select ss.id, ss.user_id, ss.fingerprint, ss.skills, ss.safe_at, ss.create_at, ss.team_id, ss.group_id, ss.is_team_leader, ss.is_group_leader, ss.job_title, su.mobile, su.email, su.real_name, su.avatar, su.work_no, su.gender, su.birth_day from sb_staff ss left join sy_user su on ss.user_id = su.id
1=1
and ss.group_id = #{groupId}
and ss.team_id = #{teamId}
and ss.job_title = #{jobTitle}
and ss.mobile like CONCAT('%',#{mobile},'%')
and ss.work_no like CONCAT('%',#{workNo},'%')
and su.real_name like CONCAT('%',#{realName},'%')

SELECT cast(ss.id as char ) as id ,
su.real_name ,
cast(ss.team_id as char) as team_id ,
cast(ss.group_id as char) as group_id , ${sql}
FROM sb_staff ss

LEFT JOIN sy_user su on su.id = ss.user_id

LEFT JOIN sb_staff_group_scheduling ssgs on ssgs.staff_group_id = ss.group_id and ssgs.date >= #{startAt} AND
ssgs.date <= #{endAt}
LEFT JOIN sb_route_item sri on sri.id = ssgs.route_item_id

    1=1

        and ss.team_id = #{teamId}


        and ss.group_id = #{groupId}


GROUP BY ss.id

SELECT sri.id route_item_id, sri.route_item_no, sri.shift_id, sri.attendance_at, sri.back_at, sri.shift_group_id, sri.distance, srtd.date, stts.staff_team_id, ssgs.staff_group_id FROM sb_route_item sri LEFT JOIN sb_route_table srt ON srt.id = sri.table_id LEFT JOIN sb_runtime_table_date srtd ON srtd.table_id = srt.runtime_table_id LEFT JOIN sb_staff_team_scheduling stts ON stts.date = srtd.date AND stts.shift_group_id = sri.shift_group_id LEFT JOIN sb_staff_group_scheduling ssgs ON ssgs.date = srtd.date AND ssgs.route_item_id = sri.id
srtd.date = #{date}
and sri.shift_id = #{shiftId}
and sri.shift_group_id = #{shiftGroupId}
and stts.staff_team_id = #{staffTeamId}
and ssgs.staff_group_id = #{staffGroupId}
and sri.route_item_no like CONCAT('%',#{routeItemNo},'%')
